import {Card, Container, Col, Row, Button} from "react-bootstrap";
import {FaGithub, FaImages} from "react-icons/fa";
import FsLightbox from 'fslightbox-react'; 
import {useState} from 'react';
import Lightbox from 'react-image-lightbox';


const images = ['./ecoslo-project/home.png'];
//will be imported in the _app component for all pages
class ProjectDetails extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            // isVisible: false
            photoIndex: 0,
            isOpen: false,
        };
    }



  render() {
    const { photoIndex, isOpen } = this.state;
    return (
        <div>
            {isOpen && (
          <Lightbox
            mainSrc={images[photoIndex]}
            nextSrc={images[(photoIndex + 1) % images.length]}
            prevSrc={images[(photoIndex + images.length - 1) % images.length]}
            onCloseRequest={() => this.setState({ isOpen: false })}
            onMovePrevRequest={() =>
              this.setState({
                photoIndex: (photoIndex + images.length - 1) % images.length,
              })
            }
            onMoveNextRequest={() =>
              this.setState({
                photoIndex: (photoIndex + 1) % images.length,
              })
            }
          />
        )}
        {/* <FsLightbox
          toggler={this.state.isVisible}
          sources={['./ecoslo-project/home.png']}
        /> */}
      <Card style={{width: '25rem', height: '30rem', overflow: 'auto'}}>
          <Card.Img style={{width: '25rem', height: '6rem'}}src="/images/solid-card-image.png" alt="Card image"/>
                <Card.ImgOverlay style={{textAlign: 'center', alignContent: 'center'}}>
                    <Card.Title>{this.props.projectName}</Card.Title>
                </Card.ImgOverlay>

          <Card.Body>
          <Card.Text style={{marginBottom: '.3rem'}}>
                  <b>Product</b>
              </Card.Text>
              <Card.Text style={{marginBottom: '.3rem'}}>
                  {this.props.productDescription}
              </Card.Text>
              <Card.Text style={{marginBottom: '.3rem'}}>
                  <b>Tech Stack</b>
              </Card.Text>
              <Card.Text style={{marginBottom: '.3rem'}}>
                  {this.props.techStackDescription}
              </Card.Text>
              <Card.Text style={{marginBottom: '.3rem'}}>
                  <b>Team Members</b>
              </Card.Text>
              <Card.Text style={{marginBottom: '.3rem'}}>
                  {this.props.teamMembersDescription}
              </Card.Text>
              <div style={{alignContent: 'center', alignItems: 'center', textAlign: 'center'}}>

                <a href={this.props.githubURL}>
                    <FaGithub  style={{width: '30px', height: '30px', color: 'black'}} />
                </a>
                <FaImages style={{width: '30px', height: '30px'}} onClick={() => this.setState({ isOpen: true })}/>
              </div>

          </Card.Body>
      </Card>
    </div>

    )
  }
}

export default ProjectDetails
